import { addToBasket } from '../Redux/Action'

const initialState = {
    basket: {
        id: 0,
    },
}

export default function BasketReducer(state = initialState, action: { type: any; }) {
    switch (action.type) {

        case addToBasket:
            return {
                ...state,
                basket: { ...state.basket, action, },
            }

        default:
            return state;
    }
}
