import {Action, addToBasket} from './action'

export interface BasketState{
    basket: object,
}

const initialState = {
    basket: {},
}



export default function BasketReducer(state:BasketState = initialState, action: Action) {
    switch (action.type) {

        case 'ADD_TO_BASKET':
            return {
                ...state,
                basket: { ...state.basket, action },
            }

        default:
            return state;
    }
}
